style:
.custom-move-to {
  background: #fbefff; /* light purple background */
  padding: 16px;
}

.custom-move-to .block-title {
  margin-bottom: 16px;
  font-weight: 600;
  color: #333;
}

.move-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(140px, 1fr));
  gap: 12px;
}

.move-card {
  background: #fff;
  padding: 16px;
  text-align: center;
  cursor: pointer;
  transition: all 0.2s ease-in-out;
  color:  #4c0b74;
  font-weight: 500;
  box-shadow: 0 2px 6px rgba(0,0,0,0.05);
  text-decoration: none;
  display: flex;
  flex-direction: column;
  align-items: center;
}

.move-card .icon {
  width: 26px;
  height: 26px;
  margin-bottom: 8px;
  color: #4c0b74;
}

.move-card:hover {
  background: #f0d8ff;
  transform: translateY(-2px);
}

script:
 frappe.call({
    method: "frappe.client.get",
        args: {
            doctype: "User",
            name: frappe.session.user,
             },
             callback(r) {
                 if(r.message) {
                    root_element.querySelector("#full_name").textContent=r.message.full_name;
                 }
             }
});
